import {useState, useEffect} from "react";



function App() {
    const [toDo, setToDo] =useState("");
    const onChange = (event) => setToDo(event.target.value);
    const onSubmit = (e) => {
        e.preventDefault();
    }
    return (
        <div>
            <form onSubmit={onSubmit}>
            <input
                onChange={onChange}
                value={toDo}
                type="text"
                placeholder="Todo AnyThing"
            />
            </form>
            <button>Add</button>
        </div>
    );

}

export default App;
