import {useParams} from "react-router-dom";
import {useEffect} from "react";

function Detail() {
    const {id} = useParams();
    const getMovie = async () => {
        const json = await (
            await fetch(`https://yts.mx/api/v2/list_movies.json?movie_id=${id}`)
        ).json();
        console.log(json);
    }
    useEffect(() => {
        getMovie();
    }, []);
    return <h1>Detail</h1>
}

export default Detail;
